// /apps/web/app/api/stream/route.ts
export const GET = async () => {
  const stream = new ReadableStream({
    start(c) {
      c.enqueue(encodeCE({ type:'agent.progress.created', data:{ ts:new Date().toISOString(), stage:'plan', note:'boot' } }))
